mapper.select:
select u.USER_ID, u.NAME, ti.NATIONALITY, ti.INFO, ti.LIKE, ti.PROFILEIMG, u.INTEREST1, u.INTEREST2 FROM USER u INNER JOIN TUTOR_INFO ti ON u.USER_ID = ti.TUTOR_ID INNER JOIN TUTOR_CLASS tc on u.USER_ID = tc.TUTOR_ID
and LOWER(u.NAME) LIKE CONCAT('%', LOWER(#{name}), '%')
and u.INTEREST1 = #{interest} OR u.INTEREST2 = #{interest}
and tc.CLASS_DATE = #{classDate} AND tc.TUTEE_ID IS NULL
ORDER BY ti.LIKE DESC LIMIT #{startData}, #{dataPerPage}
